mapper.select:
select 

from mrwdj
where FRDM_U = #{FRDM_U,jdbcType=VARCHAR}
  and RWBH_U = #{RWBH_U,jdbcType=VARCHAR}
  and RWXH_U = #{RWXH_U,jdbcType=INTEGER}
  and RWH_UU = #{RWH_UU,jdbcType=INTEGER}

mapper.delete:
delete from mrwdj
where FRDM_U = #{FRDM_U,jdbcType=VARCHAR}
  and RWBH_U = #{RWBH_U,jdbcType=VARCHAR}
  and RWXH_U = #{RWXH_U,jdbcType=INTEGER}
  and RWH_UU = #{RWH_UU,jdbcType=INTEGER}

mapper.update:
update mrwdj


    HTJYM_ = #{HTJYM_,jdbcType=VARCHAR},


    RWZT_U = #{RWZT_U,jdbcType=VARCHAR},


    WHRQ_U = #{WHRQ_U,jdbcType=DATE},


    WHSJ_U = #{WHSJ_U,jdbcType=TIME},


    WHR_UU = #{WHR_UU,jdbcType=VARCHAR},


    SJC_UU = #{SJC_UU,jdbcType=TIMESTAMP},


    JLZT_U = #{JLZT_U,jdbcType=VARCHAR},


where FRDM_U = #{FRDM_U,jdbcType=VARCHAR}
  and RWBH_U = #{RWBH_U,jdbcType=VARCHAR}
  and RWXH_U = #{RWXH_U,jdbcType=INTEGER}
  and RWH_UU = #{RWH_UU,jdbcType=INTEGER}

update mrwdj
set HTJYM_ = #{HTJYM_,jdbcType=VARCHAR},
  RWZT_U = #{RWZT_U,jdbcType=VARCHAR},
  WHRQ_U = #{WHRQ_U,jdbcType=DATE},
  WHSJ_U = #{WHSJ_U,jdbcType=TIME},
  WHR_UU = #{WHR_UU,jdbcType=VARCHAR},
  SJC_UU = #{SJC_UU,jdbcType=TIMESTAMP},
  JLZT_U = #{JLZT_U,jdbcType=VARCHAR}
where FRDM_U = #{FRDM_U,jdbcType=VARCHAR}
  and RWBH_U = #{RWBH_U,jdbcType=VARCHAR}
  and RWXH_U = #{RWXH_U,jdbcType=INTEGER}
  and RWH_UU = #{RWH_UU,jdbcType=INTEGER}
